{"customStyles":".cpq__browse-phase-container{
    background-color: #F3F3F3;
    margin-top: -8px;
    border-top: 1px solid #C9C9C9;
}
.cpq__preview-cart_close{
    flex:1 1 auto;
}
.cpq__browse_sidebar_container{
    padding-top: 20px;
    min-height: 100vh;
    background-color: #ffffff;
    border-right: 1px solid #C9C9C9;
}
.cpq__preview-cart-container{
    min-height: 100vh;
}
.omniscript-body {
    .cpq__parent-hierarchy_name,
    .cpq__flex-table-hierarchy_name {
      text-align: left;
    }
  }

  /* cpqBrowsePhaseContainer */
  .cpq__product-view_container {
    flex: 1 1 auto;
  }
  @media (max-width: 728px){
    .cpq__browse_sidebar_container{
        min-height: unset;
    }
  }
  @media (max-width: 1024px) {
    /* cpqProductFilterChild */
    .category-mobile {
      ul {
        overflow-x: auto;
        display: flex;
        height: 50px;
      }

      ul::-webkit-scrollbar {
        display: none;
      }

      li {
        display: inline;
      }

      span {
        display: inline;
      }

      .tree-selector__item-span_active {
        box-shadow: inset 0 -0.25rem 0 var(--lwc-brandPrimary, rgb(21, 137, 238));
      }
    }
  }
  /* cpqProductFilterChild */
  .custom-btn {
    width: fit-content;
    margin-left: -1px;
    border-radius: 0px;
  }

  /* cpqProductFilterChild */
  .filter-footer-btn .slds-button {
    border-radius: 0px;
  }

  /* CPQAttributeConfigFields_filters */
  span.slds-radio_faux,
  span.slds-checkbox_faux {
    margin-left: 25px !important;
  }

  .checkbox-title-style .slds-form-element__legend {
    font-size: 12px;
    color: #3e3e3c;
    font-weight: bold;
  }

  .text-attribute-title label {
    font-size: 12px;
    color: #3e3e3c;
    font-weight: bold;
    padding-bottom: 20px;
    padding-top: 0px;
  }

  .cpq-checkbox-style {
    span {
      padding-left: 0rem !important;
    }
  }

  .cpq__cart_preview {
    .cpq__preview-cart_items{
        max-height: 500px;
        overflow: scroll; 
        position: relative; 
    }
    .cpq__cart-root_checkbox{
        width: 2%;
        margin-top: 0.4rem;
        z-index: 10;
    }
    /* cpqCartErrorPanel */
    .cpq__error-panel-warning_icon{
          width:10%;
          padding-top: 0px;
          padding-left: 4px;
      }
      .cpq__cart-error-details_btn{
          font-size:12px;
      }
      .cpq__cart-error-panel_desc .slds-rich-text-editor__output span{
          font-size:14px !important;
          line-height:32px;
      }
      .cpq__cart-error-panel_desc{
          width: max-content;
          padding-left: 2px;
          padding-right: 2px;
      }
      .cpq__cart-error-details_btn{
          width: max-content;
          line-height:32px;
          flex:1 1 auto;
          padding-top: 0px;
      }
      .cpq__error-panel-warning_icon .slds-icon_container{
          height: 24px;
          width: 24px;
          padding-top: 8px;
          padding-left: 4px;
          padding-right: 4px;
      }
      .cpq__cart-error_panel{
          padding: 2px;
          padding-left: 6px;
          padding-right: 6px;
      }

