StylingConfiguration:
{"customStyles":".values{
    font-size: 14px;
    line-height: 21px;
    color: #080707;
    margin: 2px 0px;
    text-overflow: ellipsis
}

.title-text{
    font-size: 12px;
    line-height: 18px;
    color: #3E3E3C;
    margin: 2px 0px;
}

.account-title{
    font-size: 12px;
    line-height: 18px;
    color: #3E3E3C;
    margin: 2px 0px;
    width: 46px;
}

.account{
    font-size: 14px;
    line-height: 21px;
    color: #1073D0;
    margin: 2px 0px;
}


.status-approval-box{
    width: fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
    max-width: 12%;
}

.account-box{
    width: fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
    max-width: 25%;
}

.object-id-text{
    max-width: 100%;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.long-text-wrap{
    height: 20px;
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.fit-content-style{
    width:fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
}

.custom-btn{
    width:fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
}

.custom-btn .slds-button{
    border-radius: 0px;
    margin-right: -1px;
}

.btn-block{
    margin-left:auto;
    width:fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
}

.price-field{
    margin-left:auto;
}

.icon-style{
    width: fit-content;
    width: -moz-fit-content; /* Support Mozilla Firefox Browser */
    padding-right: 10px;
}

.updateApiCSS{
    display: none;
}

.mobile-state {
    display: initial;
}

.desktop-state {
    display: none;
}
.cpq__opportunity_global_header_icon {
    width:fit-content; padding-right:10px; width: -moz-fit-content;
}

@media (min-width: 1024px) {
  .mobile-state {
    display: none;
  }
  .desktop-state {
  	display: initial;
  }
}
